import React, { useContext, useEffect, useState } from "react";
import "./Navbar.css";

const Navbar = () => {
  const [newsapi, setnewsapi] = useState([]);
  useEffect(() => {
    data();
  }, []);
  const data = async () => {
    const data = async (title) => {
      const resp = await fetch(
        `https://newsapi.org/v2/everything?q=${title}&apiKey=99d8827f02bb4a65b540b12fdadce807`
      );
      const data = await resp.json();
      setnewsapi(data.articles);
    };
  };

  const {name}= useContext
  return (
    <>
      <nav className="maincontainer">
        <ul className="navbar">
          <li onClick={() => data("world")}>
            <button>World</button>
          </li>
          <li onClick={() => data("india")}>
            <button>India</button>
          </li>
          <li onClick={() => data("sports")}>
            <button>Sports</button>
          </li>
          <li onClick={() => data("bitcoin")}>
            <button>Bitcoin</button>
          </li>
          <li onClick={() => data("tesla")}>
            <button>Tesla</button>
          </li>
        </ul>
      </nav>
    </>
  );
};

export default Navbar;
