import React, { useContext, useEffect, useState } from "react";
import "./Navbar.css";
import NewsProvider from "../../provider/NewsProvider";

const Navbar = () => {
  

  const {name}= useContext(NewsProvider)
  console.log("name", name)
  const fetchData = async (title) => {
    try {
      const res = await fetch(
        `https://newsapi.org/v2/everything?q=bitcoin&apiKey=99d8827f02bb4a65b540b12fdadce807`
      );

      if (!res.ok) {
        throw new Error(`Request failed with status ${res.status}`);
      }

      const resData = await res.json();
      setNewsApi(resData.articles);
    } catch (err) {
      setError(err.message);
    }
  };
  return (
    <>
      <nav className="maincontainer">
        <ul className="navbar">
          <li onClick={() => handleFetchData("world")}>
            <button>World</button>
          </li>
          <li onClick={() => handleFetchData("india")}>
            <button>India</button>
          </li>
          <li onClick={() => handleFetchData("sports")}>
            <button>Sports</button>
          </li>
          <li onClick={() => handleFetchData("bitcoin")}>
            <button>Bitcoin</button>
          </li>
          <li onClick={() => handleFetchData("tesla")}>
            <button>Tesla</button>
          </li>
        </ul>
      </nav>
    </>
  );
};

export default Navbar;
