import { useEffect, useState } from "react";
import "./Newsapp.css";

function Newsapp() {
  const [newsData, setNewsData] = useState([]);
  useEffect(() => {
    getNewsData();
  });

  async function getNewsData() {
    const resp = await fetch(
      "https://newsapi.org/v2/everything/?q=sports&apiKey=a1af0b62d03445f8ae9eb43a20863707"
    );
    const data = await resp.json();
    setNewsData(data.articles);
  }
  return (
    <>
     <div></div>
      {newsData &&
        newsData.map((ch, index) => {
          return (
            <div key={index}>
              <div class="max-w-sm rounded overflow-hidden shadow-lg">
                <img class="w-full" src={ch.urlToImage} />
                <div class="px-6 py-4">
                  <div class="font-bold text-xl mb-2">{ch.title}</div>
                  <p class="text-gray-700 text-base">{ch.description}</p>
                </div>
                <div>
                  <a href={ch.url}>
                    <button className="newbtn">read more</button>
                  </a>
                </div>
              </div>
            </div>
          );
        })}
    </>
  );
}

export default Newsapp;
