import React, { useContext, useEffect, useState } from "react";
import "./Navbar.css";
import NewsProvider from "../../provider/NewsProvider";

const Navbar = () => {
  

  const {name}= useContext(NewsProvider)
  console.log("name", name)
  const fetchData = async (title) => {
      const res = await fetch(
        `https://newsapi.org/v2/everything?q=bitcoin&apiKey=99d8827f02bb4a65b540b12fdadce807`
      );

      if (!res.ok) {
        throw new Error(`Request failed with status ${res.status}`);
      }

      const resData = await res.json();
      
    }
  return (
    <>
      <nav className="maincontainer">
        <ul className="navbar">
          <li onClick={() => fetchData("world")}>
            <button>World</button>
          </li>
          <li onClick={() => fetchData("india")}>
            <button>India</button>
          </li>
          <li onClick={() => fetchData("sports")}>
            <button>Sports</button>
          </li>
          <li onClick={() => fetchData("bitcoin")}>
            <button>Bitcoin</button>
          </li>
          <li onClick={() => fetchData("tesla")}>
            <button>Tesla</button>
          </li>
        </ul>
      </nav>
    </>
  );
};

export default Navbar;
