import { useState } from "react";
import NewsProvider from "./NewsProvider";
function NewsState({ children }) {
  const [newsapi, setnewsapi] = useState([]);

const fetchData =async (title)=>{
    const res = await fetch("https://newsapi.org/v2/everything?q=bitcoin&apiKey=99d8827f02bb4a65b540b12fdadce807")
    const resData = await res.json()
    console.log(resData)
    setnewsapi(resData.articles);
}
  return (
    <NewsProvider.Provider value={{ newsapi, data : fetchData }}>
      {children}
    </NewsProvider.Provider>
  );
}
export default NewsState;
