import { useEffect, useState } from "react";
import "./Newsapp.css";

const Newsapp = () => {
  const [newsData, setNewsData] = useState([]);
  useEffect(() => {
    getNewsData();
  },[]);

  const getNewsData = async () => {
    const resp = await fetch(
      `https://newsapi.org/v2/everything?q=world&apiKey=99d8827f02bb4a65b540b12fdadce807`
    );
    const data = await resp.json();
    setNewsData(data.articles);
  }
  const handleNewsByTitle = async (title)=>{
    const resp = await fetch(
      `https://newsapi.org/v2/everything?q=${title}&apiKey=99d8827f02bb4a65b540b12fdadce807`
    );
    const data = await resp.json();
    console.log("data", data)
    setNewsData(data.articles);
  }
  return (
    <>
        {/* <div className="maincontainer">
          <ul className="flex space-x-10 m-5">
            <li onClick={()=>handleNewsByTitle("world")}><button>World</button></li>
            <li onClick={()=>handleNewsByTitle("india")}><button>India</button></li>
            <li onClick={()=>handleNewsByTitle("sports")}><button>Sports</button></li>
            <li onClick={()=>handleNewsByTitle("bitcoin")}><button>Bitcoin</button></li>
            <li onClick={()=>handleNewsByTitle("tesla")}><button>Tesla</button></li>

          </ul>
        </div> */}
<div class="topnav" id="myTopnav">
  <a href="#home" class="active">World</a>
  <a href="#news">India</a>
  <a href="#contact">Sports</a>
  <a href="#about"></a>Bitcoin

</div>

      <div className="container">
        {newsData &&
          newsData.map((ch, index) => {
            return (
              <div className="child" key={index}>
                <div class="max-w-sm bg-white border border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
                  <a href="#">
                    <img class="rounded-t-lg" src={ch.urlToImage} alt="" />
                  </a>
                  <div class="p-5">
                    <a href="#">
                      <h5 class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white title">
                        {ch.title}
                      </h5>
                    </a>
                    <p class="mb-3 font-normal text-gray-700 dark:text-gray-400 description">
                      {ch.description}
                    </p>
                    <a
                      href={ch.url}
                      class="inline-flex items-center px-3 py-2 text-sm font-medium text-center text-white bg-blue-700 rounded-lg hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
                    >
                      Read more
                    </a>
                  </div>
                </div>
              </div>
            );
          })}
      </div>
    </>
  );
};

export default Newsapp;
