import React, { useEffect, useState } from 'react'

const Navbar = () => {
  const [newsapi, setnewsapi]=useState([])
  useEffect(()=>{data()},[])
  const data = async()=>{
    const data = async (title)=>
    {
    const resp = await fetch(`https://newsapi.org/v2/everything?q=${title}&apiKey=99d8827f02bb4a65b540b12fdadce807`);
    const data = await resp.json();
    setnewsapi(data.articles);
    }  }
  return (
    <>
          <nav className="maincontainer">
          <ul className="navbar">
            <li onClick={()=>handleNewsByTitle("world")}><button>World</button></li>
            <li onClick={()=>handleNewsByTitle("india")}><button>India</button></li>
            <li onClick={()=>handleNewsByTitle("sports")}><button>Sports</button></li>
            <li onClick={()=>handleNewsByTitle("bitcoin")}><button>Bitcoin</button></li>
            <li onClick={()=>handleNewsByTitle("tesla")}><button>Tesla</button></li>

          </ul>
        </nav>
    </>
  )
}

export default Navbar
