import { useEffect, useState } from "react";
import "./Newsapp.css";

function Newsapp() {
  const [newsData, setNewsData] = useState([]);
  useEffect(() => {
    getNewsData();
  });

  async function getNewsData() {
    const resp = await fetch(
      "https://newsapi.org/v2/everything/?q=sports&apiKey=a1af0b62d03445f8ae9eb43a20863707"
    );
    const data = await resp.json();
    setNewsData(data.articles);
  }
  return (
    <>
     <div className="container">
      {newsData &&
        newsData.map((ch, index) => {
          return (
            <div key={index}>
              
              
<div class="max-w-sm bg-white border border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
    <a href="#">
        <img class="rounded-t-lg" src={ch.urlToImage} alt="" />
    </a>
    <div class="p-5">
        <a href="#">
            <h5 class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">{ch.title}</h5>
        </a>
        <p class="mb-3 font-normal text-gray-700 dark:text-gray-400">{ch.description}</p>
        <a href={ch.url} class="inline-flex items-center px-3 py-2 text-sm font-medium text-center text-white bg-blue-700 rounded-lg hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">
            Read more
        </a>
    </div>
</div>

            </div>
          );
        })}
    </div>
    </>
  );
}

export default Newsapp;
