import React, { useContext, useEffect, useState } from "react";
import "./Navbar.css";
import NewsProvider from "../../provider/NewsProvider";

const Navbar = () => {
  

  const {name, fetchData}= useContext(NewsProvider)
  console.log("name", name)
  function handleFetchData(title){
    fetchData(title)
  }
  return (
    <>
      <nav className="maincontainer">
        <ul className="navbar">
          <li onClick={() => handleFetchData("world")}>
            <button>World</button>
          </li>
          <li onClick={() => handleFetchData("india")}>
            <button>India</button>
          </li>
          <li onClick={() => handleFetchData("sports")}>
            <button>Sports</button>
          </li>
          <li onClick={() => handleFetchData("bitcoin")}>
            <button>Bitcoin</button>
          </li>
          <li onClick={() => handleFetchData("tesla")}>
            <button>Tesla</button>
          </li>
        </ul>
      </nav>
    </>
  );
};

export default Navbar;
