import { useState } from "react";
import NewsProvider from "./NewsProvider";
function NewsState({ children }) {
  const [newsapi, setnewsapi] = useState([]);

  const fetchDataApi = async (title) => {
    console.log(title)
    const resp = await fetch(
        "https://newsapi.org/v2/everything?q=bitcoin&apiKey=99d8827f02bb4a65b540b12fdadce807"
    //   `https://newsapi.org/v2/everything?q=${title}&apiKey=99d8827f02bb4a65b540b12fdadce807`
    );
    const data = await resp.json();
    setnewsapi(data.articles);
  };
  return (
    <NewsProvider.Provider value={{ newsapi, fetchDataApi }}>
      {children}
    </NewsProvider.Provider>
  );
}
export default NewsState;
