import { useEffect, useState } from "react";
import "./NewsData.css";

function News() {
  const [newsData, setNewsData] = useState([]);
  // const [loading, setLoading] = useState(false);
  useEffect(() => {getNewsData();});

  async function getNewsData() {
    // setLoading(true);

    const resp = await fetch("https://newsapi.org/v2/everything/?q=sports&apiKey=a1af0b62d03445f8ae9eb43a20863707");
    const data = await resp.json();
    setNewsData(data.articles);
    // setLoading(false);
  }
  return (
    <>
      <div className="grandfather">
        {newsData &&
          newsData.map((item, index) => {
            return (
              <div className="newsparent">
                <div key={index}>
                  <img className="newsimages" src={item.urlToImage} />
                  <div className="newstitile"> {item.title} </div>
                  <div className="newsdescription">{item.description}</div>
                  <div>{item.publishedAt}</div>
                  <div><a href={item.url}><button className="newbtn">read more</button></a></div>
                </div>
              </div>
            );
          })}
      </div>
    </>
  );
}

export default NewsData;
