import { useEffect, useState } from "react";
import "./Newsapp.css";

function Newsapp() {
  const [newsData, setNewsData] = useState([]);
  useEffect(() => {
    getNewsData();
  });

  async function getNewsData() {
    const resp = await fetch(
      "https://newsapi.org/v2/everything/?q=sports&apiKey=a1af0b62d03445f8ae9eb43a20863707"
    );
    const data = await resp.json();
    setNewsData(data.articles);
  }
  return (
    <>
      <div className="container">
        {newsData &&
          newsData.map((ch, index) => {
            return (
              <div key={index}>
                {/* <div class="max-w-sm rounded overflow-hidden shadow-lg">
                <img class="w-full" src={ch.urlToImage} />
                <div class="px-6 py-4">
                  <div class="font-bold text-xl mb-2 title">{ch.title}</div>
                  <p class="text-gray-700 text-base description">{ch.description}</p>
                </div>
                <div>
                  <a href={ch.url}>
                    <button className="newbtn">read more</button>
                  </a>
                </div>
              </div> */}

                <div class="max-w-sm bg-white border border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
                  <a href="#">
                    <img
                      class="rounded-t-lg"
                      src="/docs/images/blog/image-1.jpg"
                      alt=""
                    />
                  </a>
                  <div class="p-5">
                    <a href="#">
                      <h5 class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">
                        Noteworthy technology acquisitions 2021
                      </h5>
                    </a>
                    <p class="mb-3 font-normal text-gray-700 dark:text-gray-400">
                      Here are the biggest enterprise technology acquisitions of
                      2021 so far, in reverse chronological order.
                    </p>
                    <a
                      href="#"
                      class="inline-flex items-center px-3 py-2 text-sm font-medium text-center text-white bg-blue-700 rounded-lg hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
                    >
                      Read more
                      <svg
                        class="w-3.5 h-3.5 ml-2"
                        aria-hidden="true"
                        xmlns="http://www.w3.org/2000/svg"
                        fill="none"
                        viewBox="0 0 14 10"
                      >
                        <path
                          stroke="currentColor"
                          stroke-linecap="round"
                          stroke-linejoin="round"
                          stroke-width="2"
                          d="M1 5h12m0 0L9 1m4 4L9 9"
                        />
                      </svg>
                    </a>
                    
                  </div>
                </div>
              </div>
            );
          })}
      </div>
    </>
  );
}

export default Newsapp;
