import React, { useContext, useEffect, useState } from "react";
import "./Navbar.css";
import NewsProvider from "../../provider/NewsProvider";

const Navbar = () => {
  const [toggleNav, setToggleNav] = useState(false);

  const { newsapi, fetchData } = useContext(NewsProvider);
  // console.log("name", newsapi);
  
  return (
    <>
      {/* <nav className="maincontainer">
        <ul className="navbar">
          <li onClick={() => fetchData("world")}><button>World</button></li>
          <li onClick={() => fetchData("india")}><button>India</button></li>
          <li onClick={() => fetchData("sports")}><button>Sports</button></li>
          <li onClick={() => fetchData("bitcoin")}><button>Bitcoin</button></li>
          <li onClick={() => fetchData("tesla")}><button>Tesla</button></li>
          <li onClick={() => fetchData("technology")}><button>Technology</button></li>
        </ul>
      </nav> */}

<nav className="desktop">
        <div className="container">
          <p className="logo">Chandan</p>
          <ul>
              <li onClick={() => fetchData("world")}><a href="#world"></a>World</li>
              <li onClick={() => fetchData("india")}><a href="#india"></a>India</li>
              <li onClick={() => fetchData("india")}><a href="#tesla"></a>Tesla</li>
              <li onClick={() => fetchData("technology")}><a href="#technology"></a>Technology</li>
              <li onClick={() => fetchData("sports")}><a href="#sports"></a>Sports</li>
              <li onClick={() => fetchData("bitcoin")}><a href="#bitcoin"></a>Bitcoin</li>
          </ul>
        </div>
      </nav>


    {/* Define the size of mobile */}
      <nav className="mob">
        <div className="container_mob">
          <p className="logo">Chandan Sharma</p>
          <i class="ri-menu-line" onClick={() => setToggleNav(!toggleNav)}></i>
        </div>{toggleNav && (
          <div className="navitems">
            <ul>
              <li><a href="#"></a>Home</li>
              <li><a href="#"></a>Service</li>
              <li><a href="#"></a>About</li>
              <li><a href="#"></a>Resume</li>
              <li><a href="#"></a>Contact</li>
            </ul>
          </div>
        )}
      </nav>



    </>
  );
};

export default Navbar;
