    <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="0.4" d="M18 5h1v12a2 2 0 0 1-2 2m0 0a2 2 0 0 1-2-2V2a1 1 0 0 0-1-1H2a1 1 0 0 0-1 1v15a2 2 0 0 0 2 2h14ZM10 4h2m-2 3h2m-8 3h8m-8 3h8m-8 3h8M4 4h3v3H4V4Z"/>
  </svg>
  </a>
  <a onClick={()=>handleNewsByTitle("world")} href="#home" class="active">World</a>
  <a onClick={()=>handleNewsByTitle("india")} href="#news">India</a>
  <a onClick={()=>handleNewsByTitle("sports")} href="#contact">Sports</a>
